from django.http import HttpResponseRedirectfrom django.shortcuts import render, get_object_or_404from django.views import Viewfrom .models import ShortUrlfrom .forms import SubmitUrlFormclass HomeView(View):	def get(self, request, *args, **kwargs):		form = SubmitUrlForm(request.POST)		return render(request, 'shortener/home.html', {})	def post(self, request, *args, **kwargs):		form = SubmitUrlForm(request.POST)		if form.is_valid():			print(form.cleaned_data)		return render(request, 'shortener/home.html', {})class ShortRedirectView(View):	def get(self, request, shortcode=None, *args, **kwargs):		obj = get_object_or_404(ShortUrl, shortcode=shortcode)		return HttpResponseRedirect(obj.url)